import React, { useState } from "react";
import styles from "./AuthPage.module.css";

const AuthPage = () => {
  const [slide, setSlide] = useState(false);

  return (
    <div
      className={`d-flex vh-100 ${
        slide ? "justify-content-end" : "justify-content-start"
      }`}
    >
      <div
        className={`d-flex flex-column justify-content-center bg-white w-50 p-5 ${
          slide ? "d-none" : "d-flex"
        }`}
      >
        <h2>Login</h2>
        <input
          className="form-control my-2"
          type="text"
          placeholder="Username"
        />
        <input
          className="form-control my-2"
          type="password"
          placeholder="Password"
        />
        <button
          className="btn btn-primary mt-2"
          onClick={() => setSlide(!slide)}
        >
          Go to Signup
        </button>
      </div>
      <div
        className={`d-flex flex-column justify-content-center bg-info w-50 p-5 ${
          slide ? "d-flex" : "d-none"
        }`}
      >
        <h2>Signup</h2>
        <input
          className="form-control my-2"
          type="text"
          placeholder="Username"
        />
        <input
          className="form-control my-2"
          type="password"
          placeholder="Password"
        />
        <button
          className="btn btn-primary mt-2"
          onClick={() => setSlide(!slide)}
        >
          Go to Login
        </button>
      </div>
    </div>
  );
};

export default AuthPage;
